import React from 'react';
import axios from 'axios';

export default function SearchBar(){
  const [q, setQ] = React.useState('');
  const [res, setRes] = React.useState([]);
  const [prefix, setPrefix] = React.useState('');
  const [ac, setAc] = React.useState([]);

  const doSearch = async (e) => { e.preventDefault(); try { const r = await axios.get(`/search?q=${encodeURIComponent(q)}`); setRes(r.data); } catch(e){ setRes([]);} };
  const doAc = async (val) => { setPrefix(val); try { const r = await axios.get(`/autocomplete/user?prefix=${encodeURIComponent(val)}`); setAc(r.data); } catch(e){ setAc([]);} };

  return (
    <div className="border p-2 mb-2">
      <h2>Search</h2>
      <form onSubmit={doSearch}>
        <input placeholder="query" value={q} onChange={e=>setQ(e.target.value)} />
        <button type="submit">Search</button>
      </form>
      <div>Results: {res.join(', ')}</div>
      <hr/>
      <div>
        <input placeholder="username prefix" value={prefix} onChange={e=>doAc(e.target.value)} />
        <div>Autocomplete: {ac.join(', ')}</div>
      </div>
    </div>
  );
}
