import React, { useEffect, useState } from 'react';
import axios from 'axios';

function NetworkGraph() {
  const [graphData, setGraphData] = useState({ nodes: [], edges: [] });
  const [loading, setLoading] = useState(false);

  const fetchGraphData = async () => {
    setLoading(true);
    try {
      const response = await axios.get('/graph/data');
      setGraphData(response.data);
    } catch (error) {
      console.error('Error fetching graph data:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchGraphData();
  }, []);

  return (
    <div className="bg-gray-800 p-6 rounded-lg">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-bold text-blue-400 flex items-center">
          üï∏Ô∏è Social Network Graph
        </h2>
        <button
          onClick={fetchGraphData}
          className="px-4 py-2 bg-gray-700 hover:bg-gray-600 text-white rounded-lg transition-colors"
        >
          üîÑ Refresh
        </button>
      </div>

      {loading ? (
        <div className="text-gray-400 text-center py-8">Loading graph...</div>
      ) : (
        <div>
          {/* Network Stats */}
          <div className="grid grid-cols-2 gap-4 mb-6">
            <div className="bg-gray-700 p-4 rounded-lg">
              <div className="text-2xl font-bold text-blue-400">{graphData.nodes.length}</div>
              <div className="text-sm text-gray-400">Total Users</div>
            </div>
            <div className="bg-gray-700 p-4 rounded-lg">
              <div className="text-2xl font-bold text-green-400">{graphData.edges.length}</div>
              <div className="text-sm text-gray-400">Follow Connections</div>
            </div>
          </div>

          {/* User Cards */}
          <div className="space-y-4">
            <h3 className="text-lg font-semibold text-gray-300">Network Members</h3>
            {graphData.nodes.map((node) => {
              const outgoingEdges = graphData.edges.filter(e => e.source === node.id);
              const incomingEdges = graphData.edges.filter(e => e.target === node.id);

              return (
                <div key={node.id} className="bg-gray-700 p-4 rounded-lg">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center space-x-3">
                      <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white font-bold text-lg">
                        {node.name.charAt(0).toUpperCase()}
                      </div>
                      <div>
                        <div className="font-bold text-white">{node.name}</div>
                        <div className="text-xs text-gray-400">User ID: {node.id}</div>
                      </div>
                    </div>
                  </div>

                  <div className="grid grid-cols-4 gap-2 text-sm">
                    <div className="bg-gray-800 p-2 rounded text-center">
                      <div className="text-blue-400 font-bold">{node.followers}</div>
                      <div className="text-gray-400 text-xs">Followers</div>
                    </div>
                    <div className="bg-gray-800 p-2 rounded text-center">
                      <div className="text-green-400 font-bold">{node.following}</div>
                      <div className="text-gray-400 text-xs">Following</div>
                    </div>
                    <div className="bg-gray-800 p-2 rounded text-center">
                      <div className="text-purple-400 font-bold">{node.posts}</div>
                      <div className="text-gray-400 text-xs">Posts</div>
                    </div>
                    <div className="bg-gray-800 p-2 rounded text-center">
                      <div className="text-pink-400 font-bold">{node.likes}</div>
                      <div className="text-gray-400 text-xs">Likes Recv</div>
                    </div>
                  </div>

                  {/* Show connections */}
                  {outgoingEdges.length > 0 && (
                    <div className="mt-3 pt-3 border-t border-gray-600">
                      <div className="text-xs text-gray-400 mb-1">Following:</div>
                      <div className="flex flex-wrap gap-1">
                        {outgoingEdges.map((edge, idx) => {
                          const targetNode = graphData.nodes.find(n => n.id === edge.target);
                          return (
                            <span
                              key={idx}
                              className="px-2 py-1 bg-green-900 text-green-300 text-xs rounded-full"
                            >
                              ‚Üí {targetNode ? targetNode.name : `User ${edge.target}`}
                            </span>
                          );
                        })}
                      </div>
                    </div>
                  )}

                  {incomingEdges.length > 0 && (
                    <div className="mt-2">
                      <div className="text-xs text-gray-400 mb-1">Followed by:</div>
                      <div className="flex flex-wrap gap-1">
                        {incomingEdges.map((edge, idx) => {
                          const sourceNode = graphData.nodes.find(n => n.id === edge.source);
                          return (
                            <span
                              key={idx}
                              className="px-2 py-1 bg-blue-900 text-blue-300 text-xs rounded-full"
                            >
                              ‚Üê {sourceNode ? sourceNode.name : `User ${edge.source}`}
                            </span>
                          );
                        })}
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>

          {graphData.nodes.length === 0 && (
            <div className="text-gray-400 text-center py-8">
              No users in the network yet. Create some users to see the graph!
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default NetworkGraph;
