import React from 'react';
import axios from 'axios';

export default function PathExplorer(){
  const [u1, setU1] = React.useState('');
  const [u2, setU2] = React.useState('');
  const [path, setPath] = React.useState([]);
  const [recUser, setRecUser] = React.useState('');
  const [recs, setRecs] = React.useState([]);

  const findPath = async (e) => {
    e.preventDefault();
    try { const r = await axios.get(`/path?u1=${encodeURIComponent(u1)}&u2=${encodeURIComponent(u2)}`); setPath(r.data.path || []); } catch(e){ setPath([]); }
  };

  const getRecs = async (e) => {
    e.preventDefault();
    try { const r = await axios.get(`/recommendations?u=${encodeURIComponent(recUser)}`); setRecs(r.data || []); } catch(e){ setRecs([]); }
  };

  return (
    <div className="border p-2 mb-2">
      <h2>Path Explorer</h2>
      <form onSubmit={findPath} className="mb-2">
        <input placeholder="u1" value={u1} onChange={e=>setU1(e.target.value)} />
        <input placeholder="u2" value={u2} onChange={e=>setU2(e.target.value)} />
        <button type="submit">Degrees of Separation</button>
      </form>
      <div>Path: {path.length ? path.join(' → ') : '—'}</div>
      <hr/>
      <form onSubmit={getRecs} className="mt-2">
        <input placeholder="user id" value={recUser} onChange={e=>setRecUser(e.target.value)} />
        <button type="submit">People You May Know</button>
      </form>
      <div>Recommendations: {recs.join(', ')}</div>
    </div>
  );
}
