import React from 'react';
import axios from 'axios';

export default function CommunityViewer(){
  const [comms, setComms] = React.useState([]);
  const [members, setMembers] = React.useState([]);
  React.useEffect(()=>{ (async ()=>{ try { const r = await axios.get('/communities'); setComms(r.data); } catch(e){ setComms([]);} })(); }, []);
  const showMembers = async (cid) => { try { const r = await axios.get(`/community/${cid}`); setMembers(r.data); } catch(e){ setMembers([]); } };
  return (
    <div className="border p-2 mb-2">
      <h2>Community Viewer</h2>
      <ul>
        {comms.map(c => <li key={c.community_id}><button onClick={()=>showMembers(c.community_id)}>Community {c.community_id} ({c.members.length})</button></li>)}
      </ul>
      <div>Members: {members.join(', ')}</div>
    </div>
  );
}
