import React from 'react';
import axios from 'axios';

export default function GraphManager() {
  const [username, setUsername] = React.useState('');
  const [postUser, setPostUser] = React.useState('');
  const [postContent, setPostContent] = React.useState('');
  const [intUser, setIntUser] = React.useState('');
  const [intTarget, setIntTarget] = React.useState('');
  const [intType, setIntType] = React.useState('like');
  const [message, setMessage] = React.useState('');

  const addUser = async (e) => {
    e.preventDefault();
    try {
      const r = await axios.post('/user', `username=${encodeURIComponent(username)}`);
      setMessage('User created: ' + JSON.stringify(r.data));
    } catch (err) { setMessage('Error creating user'); }
  };

  const addPost = async (e) => {
    e.preventDefault();
    try {
      const body = `user_id=${encodeURIComponent(postUser)}&content=${encodeURIComponent(postContent)}`;
      const r = await axios.post('/post', body);
      setMessage('Post created: ' + JSON.stringify(r.data));
    } catch (err) {
      if (err.response && err.response.data) setMessage('Error: ' + JSON.stringify(err.response.data));
      else setMessage('Error creating post');
    }
  };

  const addInteraction = async (e) => {
    e.preventDefault();
    try {
      const body = `user_id=${encodeURIComponent(intUser)}&target_id=${encodeURIComponent(intTarget)}&type=${encodeURIComponent(intType)}`;
      const r = await axios.post('/interaction', body);
      setMessage('Interaction added: ' + JSON.stringify(r.data));
    } catch (err) { setMessage('Error adding interaction'); }
  };

  return (
    <div className="border p-2 mb-4">
      <h2 className="font-bold">Graph Manager</h2>
      <div className="grid grid-cols-3 gap-4 mt-2">
        <form onSubmit={addUser}>
          <h3>Add User</h3>
          <input placeholder="username" value={username} onChange={e=>setUsername(e.target.value)} />
          <button type="submit">Add</button>
        </form>
        <form onSubmit={addPost}>
          <h3>Add Post</h3>
          <input placeholder="user id" value={postUser} onChange={e=>setPostUser(e.target.value)} />
          <textarea placeholder="content" value={postContent} onChange={e=>setPostContent(e.target.value)} />
          <button type="submit">Post</button>
        </form>
        <form onSubmit={addInteraction}>
          <h3>Add Interaction</h3>
          <input placeholder="user id" value={intUser} onChange={e=>setIntUser(e.target.value)} />
          <input placeholder="target id" value={intTarget} onChange={e=>setIntTarget(e.target.value)} />
          <select value={intType} onChange={e=>setIntType(e.target.value)}><option>like</option><option>follow</option></select>
          <button type="submit">Add</button>
        </form>
      </div>
      <div className="mt-2">{message}</div>
    </div>
  );
}
